mapper.select:
SELECT rnum, reviewno, userid, starscore, reviewcontent, lastvisitcount
FROM (
  SELECT ROWNUM as rnum, reviewno, userid, starscore, reviewcontent, lastvisitcount
  FROM (
    SELECT reviewno, userid, starscore, reviewcontent, lastvisitcount
    FROM reviews
    ORDER BY reviewno DESC
  )
  WHERE rownum <= #{endRowNo}  
)
WHERE rnum >= #{startRowNo}

mapper.update:
UPDATE reviews SET userid=#{userid}, starscore=#{starscore}, reviewcontent=#{reviewcontent},
	       		   lastvisitcount=#{lastvisitcount}
WHERE reviewno=#{reviewno}
